import React, {Component} from 'react'
import {
    Button,
    Form,
    Grid,
    Message,
    Segment,
  } from 'semantic-ui-react';
  import { NavLink, withRouter } from 'react-router-dom';
  import {connect} from 'react-redux'
  import "./styles.css"
  import ImageMapper from 'react-image-mapper';


function Login({users}){

    function handleChange(e){
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    function logIn(e){
        e.preventDefault()


        fetch("http://localhost:3000/api/v1/login", {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                username: this.state.username,
                password: this.state.password
            })
        })
        .then(res =>  res.json())
        .then(userInfo => 
            {
                console.log(userInfo)
            localStorage.token = userInfo.token 
            localStorage.user = this.state.username 

            this.props.history.push("/profile") 
        }
        )

    }

    MAP = {
        name: "my-map",
        areas: [
          { name: "1", shape: "circle", coords: [1434,855,72], preFillColor: "green", fillColor: "blue"  },
          { name: "2", shape: "rect", coords: [398,790,786,923], preFillColor: "pink"  },
          { name: "3", shape: "circle", coords: [1637,855,75], fillColor: "yellow"  },
          { name: "4", shape: "circle", coords: [1227,847,74], preFillColor: "red"  },

        ]
      }

    return(


        <div>
            <ImageMapper src={"landing_bg.png"} map={MAP} width={500}/>
       </div>
    )

}

const mapStateToProps=state=>({
    users: state.users
})
export default connect(mapStateToProps)(Login)
